    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String texto = jTextArea2.getText();
        texto = texto.toLowerCase();
        String resultado = "";
        for (int n = 0; n < texto.length(); n++)
        {
            char a = texto.charAt(n);
            switch(a){
                case 'a':
                    resultado += ".- ";
                    break;
                case 'b':
                    resultado += "-... ";
                    break;
                case 'c':
                    resultado += "-.-. ";
                    break;
                case 'd':
                    resultado += "-.. ";
                    break;
                case 'e':
                    resultado += ". ";
                    break;
                case 'f':
                    resultado += "..-. ";
                    break;
                case 'g':
                    resultado += "--. ";
                    break;
                case 'h':
                    resultado += ".... ";
                    break;
                case 'i':
                    resultado += ".. ";
                    break;
                case 'j':
                    resultado += ".--- ";
                    break;
                case 'k':
                    resultado += "-.- ";
                    break;
                case 'l':
                    resultado += ".-.. ";
                    break;
                case 'm':
                    resultado += "-- ";
                    break;
                case 'n':
                    resultado += "-. ";
                    break;
                case 'o':
                    resultado += "--- ";
                    break;
                case 'p':
                    resultado += ".--. ";
                    break;
                case 'q':
                    resultado += "--.- ";
                    break;
                case 'r':
                    resultado += ".-. ";
                    break;
                case 's':
                    resultado += "... ";
                    break;
                case 't':
                    resultado += "- ";
                    break;
                case 'u':
                    resultado += "..- ";
                    break;
                case 'v':
                    resultado += "...- ";
                    break;
                case 'w':
                    resultado += ".-- ";
                    break;
                case 'x':
                    resultado += "-..- ";
                    break;
                case 'y':
                    resultado += "-.-- ";
                    break;
                case 'z':
                    resultado += "--.. ";
                    break;
                case '1':
                    resultado += ".---- ";
                    break;
                case '2':
                    resultado += "..--- ";
                    break;
                case '3':
                    resultado += "...-- ";
                    break;
                case '4':
                    resultado += "....- ";
                    break;
                case '5':
                    resultado += "..... ";
                    break;
                case '6':
                    resultado += "-.... ";
                    break;
                case '7':
                    resultado += "--... ";
                    break;
                case '8':
                    resultado += "---.. ";
                    break;
                case '9':
                    resultado += "----. ";
                    break;
                case '0':
                    resultado += "----- ";
                    break;
                case ' ':
                    resultado += " ";
                    break;
                case 'ñ':
                    resultado += "-. ";
                    break;
            } 
        }
        jLabel2.setText(resultado);
    }//GEN-LAST:event_jButton1ActionPerformed

